import React, { useState } from 'react';
import { fabric } from 'fabric';
import { GoogleGenerativeAI } from '@google/generative-ai';
import toast, { Toaster } from 'react-hot-toast';
import { FiDownload, FiCheck, FiX } from 'react-icons/fi';
import { motion, AnimatePresence } from 'framer-motion';
import { useDropzone } from 'react-dropzone';

// Gemini API
const genAI = new GoogleGenerativeAI(process.env.REACT_APP_GEMINI_API_KEY);

const AdGenStudio = () => {
  const [step, setStep] = useState(1);

  const [assets, setAssets] = useState({
    packshot: null,
    logo: null,
    brandColor: '#21808d'
  });

  const [campaign, setCampaign] = useState({
    headline: '',
    cta: '',
    theme: 'Clean',
    tone: 'Professional'
  });

  const [creatives, setCreatives] = useState({
    facebook: null,
    instagram: null,
    display: null
  });

  const [compliance, setCompliance] = useState(null);
  const [loading, setLoading] = useState(false);

  const [aiFeatures, setAiFeatures] = useState({
    generatedHeadlines: [],
    layoutSuggestions: [],
    generatedBackgrounds: [],
    autoLayoutEnabled: false
  });

  // Asset upload
  const onDrop = (acceptedFiles, field) => {
    const file = acceptedFiles[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setAssets(prev => ({ ...prev, [field]: e.target.result }));
        toast.success(`${field} uploaded`);
      };
      reader.readAsDataURL(file);
    }
  };

  const AssetUploadZone = ({ field, label }) => {
    const { getRootProps, getInputProps } = useDropzone({
      onDrop: (files) => onDrop(files, field)
    });

    return (
      <div
        {...getRootProps()}
        className="border-2 border-dashed border-teal-400 rounded-lg p-8 text-center cursor-pointer hover:border-teal-600 transition bg-gradient-to-br from-teal-50 to-blue-50"
      >
        <input {...getInputProps()} />
        <div className="text-4xl mb-2">üìÅ</div>
        <p className="font-semibold text-gray-700">{label}</p>
        {assets[field] && (
          <p className="text-xs text-green-600 mt-2">‚úì Uploaded</p>
        )}
      </div>
    );
  };

  // Generate AI Headlines
  const generateHeadlines = async () => {
    if (!campaign.headline) {
      toast.error('Please enter a headline first');
      return;
    }

    setLoading(true);
    try {
      const model = genAI.getGenerativeModel({ model: 'gemini-pro' });

      const prompt = `Generate 3 short alternative ad headlines for: "${campaign.headline}"`;

      const result = await model.generateContent(prompt);
      const headlines = result.response
        .text()
        .split('\n')
        .filter(Boolean)
        .slice(0, 3);

      setAiFeatures(prev => ({ ...prev, generatedHeadlines: headlines }));
      toast.success('AI Headlines Generated!');
    } catch (error) {
      toast.error('AI generation failed ‚Äî using fallback');
      setAiFeatures(prev => ({
        ...prev,
        generatedHeadlines: [
          `${campaign.headline} - Limited Offer`,
          `Buy ${campaign.headline} Today`,
          `Exclusive: ${campaign.headline}`
        ]
      }));
    } finally {
      setLoading(false);
    }
  };

  // Generate layouts
  const generateLayouts = () => {
    const fallback = [
      'Left: Logo top-left, Product center, Text bottom',
      'Center: Product center, Headline above, CTA below',
      'Right: Product left, Text right, CTA bottom-right'
    ];
    setAiFeatures(prev => ({
      ...prev,
      layoutSuggestions: fallback,
      autoLayoutEnabled: true
    }));
    toast.success('Layout suggestions ready!');
  };

  // Create creatives
  const renderCreative = (format, width, height) => {
    const canvas = new fabric.Canvas(document.createElement('canvas'), {
      width,
      height
    });

    // Background gradient
    canvas.backgroundColor = new fabric.Gradient({
      coords: { x1: 0, y1: 0, x2: width, y2: height },
      colorStops: [
        { offset: 0, color: assets.brandColor },
        { offset: 1, color: '#ffffff' }
      ]
    });

    // Product image
    fabric.Image.fromURL(assets.packshot, (img) => {
      img.scaleToWidth(width * 0.45);
      img.set({ left: width * 0.25, top: height * 0.15 });
      canvas.add(img);
    });

    // Logo
    fabric.Image.fromURL(assets.logo, (img) => {
      img.scaleToWidth(width * 0.15);
      img.set({ left: 20, top: 20 });
      canvas.add(img);
    });

    // Headline
    canvas.add(
      new fabric.Text(campaign.headline, {
        left: width * 0.05,
        top: height * 0.65,
        fontSize: 36,
        fontWeight: 'bold',
        fill: '#ffffff'
      })
    );

    // CTA
    if (campaign.cta) {
      canvas.add(
        new fabric.Text(campaign.cta, {
          left: width * 0.05,
          top: height * 0.82,
          fontSize: 22,
          fontWeight: 'bold',
          fill: '#ffd700'
        })
      );
    }

    // Save Image
    setCreatives(prev => ({
      ...prev,
      [format]: canvas.toDataURL()
    }));
  };

  const generateCreatives = () => {
    if (!assets.packshot || !assets.logo || !campaign.headline) {
      toast.error('Upload assets + headline first');
      return;
    }
    setLoading(true);

    renderCreative('facebook', 1080, 1080);
    renderCreative('instagram', 1080, 1920);
    renderCreative('display', 1200, 628);

    setLoading(false);
    setStep(3);
    toast.success('Creatives generated!');
  };

  const downloadCreative = (format) => {
    const link = document.createElement('a');
    link.href = creatives[format];
    link.download = `adgen-${format}.png`;
    link.click();
  };

  // Step Components ------------------------------

  const Step1 = () => (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <h2 className="text-3xl font-bold mb-4">üìÅ Upload Assets</h2>

      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <AssetUploadZone field="packshot" label="Product Image" />
        <AssetUploadZone field="logo" label="Brand Logo" />
      </div>

      <button
        onClick={() => setStep(2)}
        disabled={!assets.packshot || !assets.logo}
        className="w-full mt-6 bg-teal-600 text-white py-3 rounded-lg"
      >
        Continue
      </button>
    </motion.div>
  );

  const Step2 = () => (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <h2 className="text-3xl font-bold mb-4">‚úçÔ∏è Campaign Details</h2>

      <input
        type="text"
        maxLength={32}
        value={campaign.headline}
        onChange={(e) => setCampaign({ ...campaign, headline: e.target.value })}
        className="w-full border p-2 rounded mb-3"
        placeholder="Headline"
      />

      <input
        type="text"
        maxLength={18}
        value={campaign.cta}
        onChange={(e) => setCampaign({ ...campaign, cta: e.target.value })}
        className="w-full border p-2 rounded mb-3"
        placeholder="CTA"
      />

      <button
        onClick={generateHeadlines}
        className="w-full bg-purple-600 text-white py-2 rounded mb-3"
      >
        AI Generate Headlines
      </button>

      {aiFeatures.generatedHeadlines.map((h, i) => (
        <button
          key={i}
          onClick={() => setCampaign({ ...campaign, headline: h })}
          className="block w-full bg-gray-100 p-2 mb-1 rounded text-left"
        >
          ‚Ä¢ {h}
        </button>
      ))}

      <button
        onClick={generateCreatives}
        className="w-full mt-4 bg-blue-600 text-white py-3 rounded"
      >
        Generate Creatives
      </button>
    </motion.div>
  );

  const Step3 = () => (
    <motion.div initial={{ opacity: 0 }} animate={{ opacity: 1 }}>
      <h2 className="text-3xl font-bold mb-6">üëÅÔ∏è Preview & Download</h2>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {['facebook', 'instagram', 'display'].map((format) => (
          <div key={format} className="bg-white shadow rounded-lg p-4">
            {creatives[format] ? (
              <img src={creatives[format]} alt="" className="w-full" />
            ) : (
              <p>Loading...</p>
            )}

            <button
              onClick={() => downloadCreative(format)}
              className="w-full mt-3 bg-teal-600 text-white py-2 rounded"
            >
              <FiDownload /> Download
            </button>
          </div>
        ))}
      </div>
    </motion.div>
  );

  // Return -------------------------------
  return (
    <div className="bg-gray-100 min-h-screen p-6">
      <Toaster />

      <div className="max-w-6xl mx-auto bg-white rounded-lg shadow p-6">
        <AnimatePresence mode="wait">
          {step === 1 && <Step1 />}
          {step === 2 && <Step2 />}
          {step === 3 && <Step3 />}
        </AnimatePresence>
      </div>
    </div>
  );
};

export default AdGenStudio;
